<?php

function create_user($email, $firstname, $lastname, $password, $organisation): bool
{
    $email = checkInput('DEFAULT', $email);
    $firstname = checkInput('DEFAULT', $firstname);
    $lastname = checkInput('DEFAULT', $lastname);
    $organisation = checkInput('DEFAULT', $organisation);

    global $conn;

    $username = strtolower($firstname).'.'.strtolower($lastname);

    $query = 'INSERT INTO `'.DATABASE_PREFIX."users` (`id`, `username`, `first_name`, `last_name`, `email`, `password`, `user_key`, `last_login_ip`, `auth_code`, `role_id`, `first_login`, `bio`, `organisation`, `website`, `profile_photo`, `views`, `edits`, `approvals`) VALUES (NULL, '".$username."', '".$firstname."', '".$lastname."', '".$email."', '".$password."', '0', '0', NULL, '1', '1', NULL, '".$organisation."', NULL, 'data:image/png;base64,', '0', '0', '0')";

    if (mysqli_query($conn, $query)) {
        return true;
    } else {
        return false;
    }
}
